import { Button, TextField } from "@mui/material";
import LoadingButton from '@mui/lab/LoadingButton';
import { Navigate } from "react-router-dom";
import React from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { loginData } from "../Redux/Action/action";
import * as Yup from "yup";
import { Form, Formik } from "formik";
import { ToastContainer, toast } from 'react-toastify';


export const SigninSchema = Yup.object().shape({
  username: Yup.string().min(2, "Too Short!").required("Required"),
  password: Yup.string().min(2, "Too Short!").required("Required"),
});

class Login extends React.Component {
  constructor() {
    super();
    this.state={
      user:false,
    }
  }


  render() {
    return (
      <>
        <div className="form">
          <ToastContainer/>
        <h2>SignIn </h2>
          <Formik
            initialValues={{
              username: "",
              password: "",
            }}
            validationSchema={SigninSchema}
            onSubmit={(values, setSubmitting ,{resetForm}) => {
              this.props.dispatch(loginData(values))
              setSubmitting(false)
              if(this.props.authData?.error)
                toast.error(this.props.authData?.error, {
                  position: "top-right",
                  autoClose: 3000,
                  hideProgressBar: false,
                });
            }}
          >
            {({ errors, touched, values, handleChange, handleBlur, isSubmitting }) => (

              <Form>
                {console.log(45455,isSubmitting)}
                <div className="form-field">
                  <TextField
                    error={errors.username && touched.username ? true : false}
                    id="username"
                    name="username"
                    label="User Name"
                    size="small"
                    variant="outlined"
                    fullWidth
                    value={values.username}
                    onBlur={handleBlur}
                    onChange={handleChange}
                    helperText={errors.username && touched.username && errors.username}
                  />
                </div>

                <div className="form-field">
                <TextField
                    error={errors.password && touched.password ? true : false}
                    id="password"
                    name="password"
                    label="Password"
                    size="small"
                    type='password'
                    variant="outlined"
                    fullWidth
                    value={values.password}
                    onBlur={handleBlur}
                    onChange={handleChange}
                    helperText={errors.password && touched.password && errors.password}
                  />
                </div>
                <div className="form-field">
                  {this.state.error && <div className=''>{this.state.error}</div>}
                  <LoadingButton variant="contained" type="submit" loading={isSubmitting}>
                    Submit
                  </LoadingButton>
                </div>
                <p>
                  If you have allReady Account. Please <a href="/register">Register</a>
                </p>
              </Form>
            )}
          </Formik>
        </div>
      </>
    );
  }
}

const mapStateToProps = (state) => ({ authData: state.userReducer });

function mapDispatchToProps(dispatch) {
  let actions = bindActionCreators({ loginData });
  return { ...actions, dispatch };
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
