import { ERROR, LOGIN_SUCCESS , REGISTER_SUCCESS ,LOGOUT_SUCCESS } from "../actionType"

const initialState = {
    data :[],
    isAuth: localStorage.getItem('token') ?? false
}


const userReducer = (state=initialState,action) =>{
    switch(action.type){
        case LOGIN_SUCCESS :
            let token = action.payload.data.token;
            localStorage.setItem("token",token);
        return{
            ...state,
            isAuth: token,
            login_data:action.payload
        }
        case REGISTER_SUCCESS :
        return{
            ...state,
            data:action.payload
        }
        case LOGOUT_SUCCESS :
            localStorage.clear()
        return{
            ...state,
            isAuth: false,
            data:action.payload
        }
        case ERROR :
            return{
                ...state,
                error : action.payload
            }
        default:
            return state
    }
}

export default userReducer;
