import { Button, TextField } from "@mui/material";
import { Navigate } from "react-router-dom";
import React from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { loginData } from "../Redux/Action/action";
import * as Yup from "yup";
import { Form, Formik } from "formik";

export const SigninSchema = Yup.object().shape({
  username: Yup.string().min(2, "Too Short!").required("Required"),
  password: Yup.string().min(2, "Too Short!").required("Required"),
});

class Login extends React.Component {
  constructor() {
    super();
    this.state={
      user:false
    }
  }

  render() {
    let status = this.props.state?.userReducer?.login_data?.status
    if(status===200){
      // this.setState({...this.state, user:true})
      window.location = '/dashboard'
    }

    return (
      <>
      {this.state.user && (
          <Navigate to="/dashboard" replace={true} />

        )}
        <div className="form">
        <h2>SignIn </h2>
          <Formik
            initialValues={{
              username: "",
              password: "",
            }}
            validationSchema={SigninSchema}
            onSubmit={(values ,{resetForm}) => {
              this.props.dispatch(loginData(values))
              // resetForm({ values: "" });
            }}
          >
            {({ errors, touched, values, handleChange, handleBlur }) => (

              <Form>
                {
                  console.log(123,errors, touched)
                }
                <div className="form-field">
                  <TextField
                    error={errors.username && touched.username ? true : false}
                    id="username"
                    name="username"
                    label="User Name"
                    size="small"
                    variant="outlined"
                    fullWidth
                    value={values.username}
                    onBlur={handleBlur}
                    onChange={handleChange}
                    helperText={errors.username && touched.username && errors.username}
                  />
                </div>

                <div className="form-field">
                <TextField
                    error={errors.password && touched.password ? true : false}
                    id="password"
                    name="password"
                    label="Password"
                    size="small"
                    variant="outlined"
                    fullWidth
                    value={values.password}
                    onBlur={handleBlur}
                    onChange={handleChange}
                    helperText={errors.password && touched.password && errors.password}
                  />
                </div>
                <div className="form-field">
                  <Button variant="contained" type="submit">
                    Login
                  </Button>
                </div>
                <p>
                  If you have allReady Account. Please <a href="/register">Register</a>
                </p>
              </Form>
            )}
          </Formik>
        </div>
      </>
    );
  }
}

const mapStateToProps = (state) => ({ state });

function mapDispatchToProps(dispatch) {
  let actions = bindActionCreators({ loginData });
  return { ...actions, dispatch };
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
