import { Button, TextField } from "@mui/material";
import { ErrorMessage, Field, Form, Formik } from "formik";
import { Navigate } from "react-router-dom";
import React from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { registerData } from "../Redux/Action/action";
import { SignupSchema } from "../schemaValidation";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

class Register extends React.Component {
  constructor(props) {
    super(props);
    this.state={
      user:false,
      bool:false
    }
  }

  componentDidMount(){
    console.log(123,"123");
  }

  render() {
    let status = this.props.state?.userReducer?.data?.status;
    if(status===200){
      toast.success('Register Successfully')    
      this.setState(!this.state.bool)
    }  

    return (
      <>
      {this.state.user && (
        <Navigate to="/" replace={true} />
      )}
        <div className="form">
        <ToastContainer />
          <Formik
            initialValues={{
              username: "",
              email: "",
              password: "",
            }}
            validationSchema={SignupSchema}
            onSubmit={(values , {resetForm}) => {
              console.log(123123,values)
              this.props.dispatch(registerData(values))
              resetForm({ values: "" });
            }}
          >
            {({ errors, touched, values, handleChange }) => (
              <Form>
                <div className="form-field">
                  <TextField
                    error={errors.username && touched.username}
                    id="username"
                    name="username"
                    label="User Name"
                    size="small"
                    variant="outlined"
                    fullWidth
                    onBlur={handleChange}
                    onChange={handleChange}
                    helperText={errors.username && errors.username}
                  />
                </div>

                <div className="form-field">
                  <TextField
                    error={errors.email && touched.email}
                    id="email"
                    name="email"
                    label="Email"
                    size="small"
                    variant="outlined"
                    fullWidth
                    onBlur={handleChange}
                    onChange={handleChange}
                    helperText={errors.email && errors.email}
                  />
                </div>

                <div className="form-field">
                <TextField
                    error={errors.password && touched.password}
                    id="password"
                    name="password"
                    label="Password"
                    size="small"
                    variant="outlined"
                    fullWidth
                    onBlur={handleChange}
                    onChange={handleChange}
                    helperText={errors.password && errors.password}
                  />
                </div>
                <div className="form-field">
                  <Button variant="contained" type="submit">
                    Register
                  </Button>
                </div>
                <p>
                  If you have allReady Account. Please <a href="/">Login</a>
                </p>
              </Form>
            )}
          </Formik>
        </div>

      </>
    );
  }
}
const mapStateToProps = ((state) =>({state}))


function mapDispatchToProps(dispatch) {
  let actions = bindActionCreators({ registerData });
  return { ...actions, dispatch };
}

export default connect(mapStateToProps, mapDispatchToProps)(Register);
